import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import Card from "../UI/Card";
import ExpensesFilter from "../NewExpenses/ExpenseFilter";
import React,{ useState } from "react";

export default function Expenses(Props){
  
  const [years,setyear]=useState("2021");
  
  const Dropdownhandler=(year)=>
  {
    setyear(year);
  }
  
  return (
    <Card className="expenses">
      
      {Props.arr.map(element=>{<ExpenseItem title={element.title} amount={element.amount} date={element.date} />})}
      <ExpensesFilter defaultyear={years} importyear={Dropdownhandler}></ExpensesFilter>

      <ExpenseItem title={Props.arr[0].title} amount={Props.arr[0].amount} date={Props.arr[0].date}/>
      <ExpenseItem title={Props.arr[1].title} amount={Props.arr[1].amount} date={Props.arr[1].date}/>
      <ExpenseItem title={Props.arr[2].title} amount={Props.arr[2].amount} date={Props.arr[2].date}/>
      <ExpenseItem title={Props.arr[3].title} amount={Props.arr[3].amount} date={Props.arr[3].date}/>
    
    </Card>
  )

}
