import { useState } from 'react';
import './ExpenseForm.css';


export default function ExpenseForm(){
const [title,setTitle]=useState('');
const [Amount,setAmount]=useState('');
const [Date,setDate]=useState('');

//Using single useState
// const [obj,changedobj]=useState({ChangedTitle : '',
// ChangedAmount :'',
// ChangedDate : ''})
const Aftersubmit=()=>{
  const obj={Subtitle:title,
    Subamount:Amount,
    Subdate:new Date(Date)}
    console.log(obj);
}


    function TitleChange(event)
    {
       setTitle(event.target.value);
      // changedobj({...obj,ChangedTitle:event.target.value});
      

    }
    function AmountChange(event)
    {
       setAmount(event.target.value);
      // changedobj({...obj,ChangedAmount:event.target.value});
    }
    function DateChange(event)
    {
       setDate(event.target.value);
      // changedobj({...obj,ChangedDate:event.target.value});
    }

  return (

      <form onSubmit={Aftersubmit}>
        <div className='new-expense__controls'>
          <div className='new-expense__control'>
            <label>Title</label>
            <input type='text' onChange={TitleChange}></input>
          </div>
          <div className='new-expense__control'>
            <label>Date</label>
            <input type='Date' min='2019-01-01' max='2022-12-31' onChange={AmountChange}></input>
          </div>
          <div className='new-expense__control'>
            <label>Amount</label>
            <input type='Number' min='0.01' step='0.01' onChange={DateChange}></input>
          </div>
        </div>
        <div className='new-expense__actions'>
          <button type='submit'>Submit</button>
        </div>
      </form>
  
  )
}
